async function get_categories() {
  let myVal
  const response = await fetch('http://192.168.0.24:5000/weather')
    .then((response) => response.json())
    .then((data) => {
       myVal = data
       console.log('Success:', data);
        });
    return myVal
   }

function create_chart(input) {
  var chart = Highcharts.chart('chart', {
    chart: {
        zoomType: 'xy'
    },
    title: {
        text: 'Temperature and Rainfall in Sandhausen'
    },
    xAxis: [{
        max: 8,
        categories: input[0],
        crosshair: true
    }],
    yAxis: [{ // Primary yAxis
        labels: {
            format: '{value}°C',
            style: {
                color: Highcharts.getOptions().colors[1]
            }
        },
        title: {
            text: 'Temperature',
            style: {
                color: Highcharts.getOptions().colors[1]
            }
        }
    }, { // Secondary yAxis
        title: {
            text: 'Rainfall',
            style: {
                color: Highcharts.getOptions().colors[0]
            }
        },
        labels: {
            format: '{value} mm',
            style: {
                color: Highcharts.getOptions().colors[0]
            }
        },
        opposite: true
    }],
    tooltip: {
        shared: true
    },
    legend: {
        layout: 'vertical',
        align: 'left',
        x: 120,
        verticalAlign: 'top',
        y: 100,
        floating: true,
        backgroundColor:
            Highcharts.defaultOptions.legend.backgroundColor || // theme
            'rgba(255,255,255,0.25)'
    },
    series: [{
        name: 'Rainfall',
        type: 'column',
        yAxis: 1,
        data: input[2],
        tooltip: {
            valueSuffix: ' mm'
        }

    }, {
        name: 'Temperature',
        type: 'spline',
        data: input[1],
        tooltip: {
            valueSuffix: '°C'
        }
    }]
});
return chart
}

(async () => {
  // main logic
  console.log('First Function:');
  const data = await get_categories();

  console.log('Second Function:');
  const chart = create_chart(data);
  })();
